// import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
// import Home from './Home';
import Layout from "./Layout";
import NavBar from "./Navbar";
import Sidebar from "./Sidebar";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
  let navigate = useNavigate();

  // VARIABLE USERNAME ET SA VALEUR INITIALE
  const [username, setLogin] = useState("");
  const handleLogin = (event) => {
    event.preventDefault();
    setLogin(event.target.value);
  };

  // VARIABLE PASSWORD ET SA VALEUR INITIALE
  const [password, setPassword] = useState("");
  const handlePassword = (event) => {
    event.preventDefault();
    setPassword(event.target.value);
  };

  // FONCTION DE CONNECTION
  const handleConnection = async (event) => {
    event.preventDefault();
    await axios
      .post("http://localhost:8000/api/login", {
        email: username,
        password: password,
      })
      .then(async (res) => {
        if (res.data.token) {
          // let promise = new Promise((resolve, reject) => {
          //   setTimeout(() => {
          //     localStorage.setItem(
          //       "moov_ged_token",
          //       JSON.stringify({
          //         token: res.data.token,
          //         id: res.data.user.id,
          //       })
          //     );
          //     resolve(true);
          //   }, 1000);
          // });

          let result = await promise;

          if (result) {
            navigate("/requetes");
          }
        } else {
          setLoading(false);
          alert(res.data);
          reset();
        }
      });
  };
  const reset = () => {
    setLogin("");
    setPassword("");
  };

  return (
    <div className="Login flex justify-center bg-gradient-to-r from-violet-500 to-fuchsia-500 h-screen">
      {/* <Layout> */}
        <form className="bg-gray-100 rounded-lg w-5/12 h-4/6 text-center mt-32">
          <div className="mt-16">
            <label className="my-6 pt-10 font-bold uppercase">E-mail</label><br/>
            <input className="pt-6 w-9/12 rounded-lg text-violet-900 hover:bg-gradient-to-r from-fuchsia-500 to-violet-500 hover:text-violet-700 border-r-violet-900" placeholder="email@example.com"/>
          </div>
          <div className="mt-16">
            <label className="my-6 pt-6 font-bold uppercase">Password</label><br/>
            <input className="pt-6 w-9/12 rounded-lg hover:bg-gradient-to-r from-fuchsia-500 to-violet-500 hover:text-violet-700 border-r-violet-900" placeholder="Enter Password"/>
          </div>
          <div className="mt-16">
            <button className="font-bold bg-violet-500 w-32 p-4 rounded-lg hover:bg-gradient-to-r from-violet-800 to-purple-800 uppercase">Submit</button>
          </div>
        </form>
      {/* </Layout> */}
    </div>
    );
}

export default Login;
