import { useNavigate } from "react-router-dom";
import React, { useState } from "react";
import { Link} from "react-router-dom";

function Login() {
  let navigate = useNavigate();

  // FONCTION DE CONNECTION
  const handleConnection = async (event) => {
    event.preventDefault();
    var pwd = document.getElementById("pwd").value;
    var pswd = document.getElementById("pswd").value;

    if (pwd != pswd) {
      alert("no match")
    } else {
        navigate('/home');
    }
  };

  const [email, setemail] = useState();
  const [error, seterror] = useState();
  const regex = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;

  // FONCTION DE VALIDATION DE L'EMAIL
  const checking = (e) => {
    e.preventDefault();
    if (regex.test(email) === false) {
      seterror("Invalid email");
    } else {
      seterror('');
      return true;
    }
    setemail(e.target.value)
  }

  const handlepwd = (e) => {
    e.preventDefault();
    setpwd(e.target.value)
  }

  const checkingpwd = () => {
    var pwd = document.getElementById("pwd").value;
    var pswd = document.getElementById("pswd").value;
    if (pwd != pswd) {
      alert("Passwords do not match")
    }
  }

  const [password, setpwd] = useState();

  return (
    <div className="Login bg-gradient-to-r from-violet-500 to-fuchsia-500 h-screen">
      {/* <Layout> */}
      {/* REGISTER & LOGIN */}
      <div className="flex justify-center">
        <div className="font-semibold uppercase text-violet-800 text-3xl">Login</div>
        {/* REGISTER */}
        <Link
          to="/register"
          className="font-semibold uppercase text-violet-800 text-3xl">
          <span className="">Register</span>
        </Link>
      </div>

      <div>

      </div>
      {/* </Layout> */}
    </div>
  );
}

export default Login;
