import Layout from "../components/Layout";
import anime from "animejs/lib/anime.es.js"
import { useState, useRef, useEffect } from "react";
import "./Home.css";

const ticks = Array.from(Array(8));
function Home() {
  const [playing, setPlaying] = useState(false);
  const animation = useRef(null);

  const handleClick = () => {
    playing ? animation.current.pause() : animation.current.play();
    setPlaying(!playing);
  };

  useEffect(() => {
    animation.current = anime.timeline({
      direction: "alternate",
      loop: true,
      autoplay: false,
      easing: "easeInOutSine"
    });

    for (const tick in ticks) {
      animation.current.add(
        {
          targets: `.dots li:nth-child(${Number(tick) + 1})`,
          scaleY: 1.5 + Math.random() * 4,
          duration: 300 + Math.random() * 300
        },
        Math.random() * 600
      );
    }
  }, []);

  return (
    <div className="Home">
      <Layout>

        <h1 className="text-6xl text-center text-6xl hover:transition ease-in-out delay-150 hover:-translate-y-1 hover:scale-110 duration-300">Hello world!</h1>
        <br /><hr /><br />

        <div class="flex justify-center">
          <h1 className="bg-emerald-100 rounded-lg w-28 text-2xl animate-pulse ring-2 ring-purple-500">Meowww!</h1><br /><br /><br />
        </div>

        <div class="flex justify-center">
          <h1 className="text-center bg-emerald-100 rounded-full w-10 h-10 mt-10 text-4xl rotate-90 ring-2 ring-purple-500">:)</h1><br /><br /><br />
        </div>

        <div class="flex justify-center">
          <h1 className="flex justify-center bg-emerald-100 rounded-full w-20 h-20 pt-4 mt-10 text-4xl animate-bounce ring-2 ring-purple-500"> ^_^ </h1>
        </div>

        <div class="flex justify-center pb-20">
          <button class="bg-violet-400 opacity-50 ring-2 mt-10 ring-purple-500 h-10 w-20 mt-20 rounded-lg hover:shadow-2xl hover:shadow-emerald-900 transition ease-in-out delay-150 hover:-translate-y-1 hover:scale-110 duration-300">Just click</button>
        </div>

        <div className="flex flex-column justify-center items-center">
          <ul className="flex items-center h-">
            {ticks.map((_, i) => (
              <li key={i} />
            ))}
          </ul>
          <button onClick={handleClick}>{playing ? "Pause" : "Play"}</button>
        </div>

      </Layout>
    </div>
  );
}

export default Home;
